SELECT * 
FROM (
	SELECT ROWNUM RN, A.* 
		FROM (
				SELECT * 
				FROM recipe 
				where title like '%' || #{keyword} || '%'
				) A
		)
WHERE RN BETWEEN #{pagingVO.start} AND #{pagingVO.end}


select b.*
from(
        select * 
        from recipe
    )b
where rownum <= 5



select count(*)
from recipe r, foodCategory b
where r.foodcategoryno = b.no and b.no = #{no}


select r.*
from recipe r, foodCategory b
where r.foodcategoryno = b.no and b.no = #{no}
